import React from 'react'
import { useState } from 'react'
import "./acordion.css"

const Acordionqlo = () => {

    const [selected, setSelected] = useState(null)

    const toggle = (i) => {
            if(selected ==i){
                return setSelected(null)
            }
            setSelected(i)
    }
  return (
    <div className="wrapper">
        <div className='accordion'>
           {data.map((item, i) => {
            return (
                <div className="item">
                    <div className="title" onClick={()=> toggle(i)}>
                      <h2>{item.question}</h2>  
                      <span> {selected=== i ? "-" : "+"} </span>
                    </div>
                    <div className={selected === i ? "content show": "content"}>
                        {item.answer}
                    </div>
                </div>
            )
           })}
        </div>
    </div>
  )
}
const data = [
    {question:"¿Qué es UNICEF Heroes Run", answer:"Es una serie de corridas familiares organizadas por UNICEF para promover los derechos de la infancia presentadas por los conocidos superhéroes de DC Comics, Wonder Woman, Batman, Superman y la Liga de la Justicia."},
    {question:"¿Cuándo y dónde se realizará UNICEF Heroes Run?", answer:"La primera corrida será presentada por la Mujer Maravilla ("}
]


export default Acordionqlo
