import React, { useRef, useState } from "react";
import {
  Animator,
  ScrollContainer,
  ScrollPage,
  batch,
  Fade,
  FadeIn,
  FadeOut,
  Move,
  MoveIn,
  MoveOut,
  Sticky,
  StickyIn,
  StickyOut,
  Zoom,
  ZoomIn,
  ZoomOut,
} from "react-scroll-motion";
import "./App.css";
import "animate.css/animate.min.css";
import Texto from "./Texto";
import heroesrun from "./assets/Logos_HeroesRun-04.png";
import frase from "./assets/Frase.png";
import concept11 from "./assets/concept11.png";
import Concept4 from "./assets/Concept4.png";
import ss from "./assets/SS.png";
import concept6 from "./assets/Concept6.png";
import botonweb2 from "./assets/botonweb2.png";
import botonweb from "./assets/botonweb.png";
import legal from "./assets/LEGAL.png";
import uniceffrase2 from "./assets/unicef-frase-2.svg";
import calendario from "./assets/calendario.svg";
import { AnimationOnScroll } from "react-animation-on-scroll";
import imagenFondoNegro1 from "./assets/imagenFondoNegro1.svg";
import imagenFondoNegro2 from "./assets/imagenFondoNegro2.svg";
import imagenFondoNegro3 from "./assets/imagenFondoNegro3.svg";
import imagenFondoNegro4 from "./assets/imagenFondoNegro4.svg";
import unicef_kit from "./assets/unicef_kit.svg";
import polera_batman from "./assets/polera_batman.jpg";
import polera_liga from "./assets/polera_liga.jpg";
import polera_superman from "./assets/polera_superman.jpg";
import polera_wonderwoman from "./assets/polera_wonderwoman.jpg";
import preguntas from "./assets/faqs.svg";
function App() {
  const ref1 = useRef(null);
  const ZoomInScrollOut = batch(StickyIn(), FadeIn(), ZoomIn());
  const handleClick = (ref) => {
    ref.current?.scrollIntoView({ behavior: "smooth" });
  };
  const [width, setWindowWidth] = useState(0);
  const updateDimensions = () => {
    const width = window.innerWidth;
    setWindowWidth(width);
  };
  const on_click = () => {
    window.location.href =
      "https://ticketplus.cl/events/unicef-heroes-run-wonder-woman";
  };
  const on_click2 = () => {
    window.location.href =
      "https://ticketplus.cl/events/unicef-heroes-run-batman";
  };
  const on_click3 = () => {
    window.location.href =
      "https://ticketplus.cl/events/unicef-heroes-run-superman";
  };
  const on_click4 = () => {
    window.location.href =
      "https://ticketplus.cl/events/unicef-heroes-run-La-Liga-de-la-Justicia";
  };

  return (
    <ScrollContainer>
      <div className="row intro mb-5">
        {/* fin nav */}
        {/*       animation={batch(StickyIn(), FadeIn(), ZoomIn())} */}
        <div className="col-xs-12 col-sm-5 col-md-5 container">
          <img src={heroesrun} className="w-full display-block" alt="" />
        </div>
        <div className="col-xs-12 col-sm-4 col-sm-offset-3 col-md-4 col-md-offset-3 blue-bg mt-3">
          <img
            src={frase}
            className="w-full display-block "
            style={{ width: "300px", heigth: "100%" }}
            alt=""
          />
        </div>
        {/* main images */}

        <div className="row center-xs p-x-0 mb-5 ">
          <div className="col-xs-12 col-sm-6 col-md-3 p-x-0">
            <img src={concept11} className="w-full display-block" alt="" />
          </div>

          <div className="col-xs-12 col-sm-6 col-md-3 p-x-0">
            <img src={Concept4} className="w-full display-block" alt="" />
          </div>
          <div className="col-xs-12 col-sm-6 col-md-3 p-x-0">
            <img src={ss} className="w-full display-block" alt="" />
          </div>
          <div className="col-xs-12 col-sm-6 col-md-3 p-x-0">
            <img src={concept6} className="w-full display-block" alt="" />
          </div>
        </div>

        <div className="container text-center my-5  ">
          <div className="row">
            <div className="col-md-12">
              <img src={uniceffrase2} />
            </div>
            {/*mucho texto */}
            <AnimationOnScroll animateIn="animate__bounceIn">
              <h4 className="mt-3 container">
                Entre diciembre de 2022 y febrero de 2023, por primera vez en
                Chile, las ciudades de Santiago, La Serena, Villarrica y Viña
                del Mar recibirán a miles de superhéroes sin capa que
                participarán de la corrida “UNICEF Heroes Run” con distancias de
                2 y 5 km, en una iniciativa de UNICEF que busca sensibilizar, y
                promover los derechos de la infancia. Deportistas, aficionados,
                familias, niños y niñas de todo el país podrán ser parte de esta
                actividad solidaria que servirá para recaudar fondos y seguir
                financiando los proyectos que UNICEF implementa en Chile y en el
                mundo. Se trata de cuatro corridas, en cuatro ciudades de Chile,
                inspiradas en distintos personajes de la “Liga de la Justicia”
                de DC Comics los que correrán para defender los derechos de
                todos los niños, niñas y adolescentes. Es así, como la primera
                corrida será presentada por la{" "}
                <b>
                  Mujer Maravilla (Wonder Woman), la que se realizará el próximo
                  11 de diciembre en Santiago,
                </b>{" "}
                en el Ciudad Empresaria, Huechuraba a las 8:30 hrs, en circuitos
                de 2k y 5k. El{" "}
                <b>
                  22 de enero del 2023 nos trasladamos a Villarrica, para la
                  corrida que será presentada por Batman. Mientras que el 5 de
                  febrero de 2023 en El Faro de La Serena,
                </b>{" "}
                estará será el turno del hombre de acero <b>Superman</b> y para
                finalizar el <b>19 de febrero,</b> 2023 en el{" "}
                <b>Sporting de Viña del Mar,</b> con la corrida que reunirá a
                todos los superhéroes de DC en la <b>Liga de la Justicia.</b>{" "}
                Comprando tu inscripción en Ticketplus estarás apoyando el
                trabajo continuo de UNICEF en favor de los derechos de los
                niños, niñas y adolescentes, que permitirá seguir respondiendo a
                las necesidades de la infancia en Chile y el mundo. Conoce aquí
                todo lo que hemos hecho en el 2021. Cada inscripción permite a
                UNICEF llevar programas de protección, salud, educación y
                respuesta a las emergencias globales para niños, niñas y
                adolescentes.
              </h4>
            </AnimationOnScroll>
            {/* fin mucho texto */}
          </div>
        </div>

        {/* background negro */}
        <div
          className="container text-center fondoNegro mt-5"
          style={{ backgroundImage: "black" }}
        >
          <div className="row">
            <div className="col-md-12">
              <img src={calendario} />
            </div>
          </div>

          <div className="container text-center">
            <div className="row my-4">
              <div className="col-md-6">
                <img
                  src={imagenFondoNegro1}
                  onClick={() => {
                    on_click();
                  }}
                />
              </div>
              <div className="col-md-6">
                <img
                  src={imagenFondoNegro2}
                  onClick={() => {
                    on_click2();
                  }}
                />
              </div>
            </div>
            <div className="row my-4">
              <div className="col-md-6">
                <img
                  src={imagenFondoNegro3}
                  onClick={() => {
                    on_click3();
                  }}
                />
              </div>
              <div className="col-md-6">
                <img
                  src={imagenFondoNegro4}
                  onClick={() => {
                    on_click4();
                  }}
                />
              </div>
            </div>
          </div>
        </div>
        <div className="text-center mt-5 container">
          <img src={unicef_kit} style={{ width: "100%" }} />
        </div>
        {/* make a 4 images */}
        <div className="container text-center my-5">
          <div className="row">
            <div className="col-md-12">
              <img src={polera_wonderwoman} style={{ width: "500px" }} />
            </div>
          </div>
          <div className="row">
            <div className="col-md-12">
              <img src={polera_batman} style={{ width: "500px" }} />
            </div>
          </div>
          <div className="row">
            <div className="col-md-12">
              <img src={polera_superman} style={{ width: "500px" }} />
            </div>
          </div>
          <div className="row">
            <div className="col-md-12">
              <img src={polera_liga} style={{ width: "500px" }} />
            </div>
          </div>
        </div>
        {/* footer  */}

        {/*Faqs section*/}
        <div className="container text-center fondoNegro my-5">
          <div className="row">
            <div className="col-md-12">
              <img src={preguntas} />
            </div>
          </div>
                  {/*make accordion*/ }
          <div className="container text-center my-5">
            <div className="row">
              <div className="col-md-12">
                <Accordion defaultActiveKey="0">
                  <Card>
                    <Accordion.Toggle as={Card.Header} eventKey="0">
                      ¿Qué es la UNICEF Heroes Run?
                    </Accordion.Toggle>
                    <Accordion.Collapse eventKey="0">
                      <Card.Body>
                        La UNICEF Heroes Run es una iniciativa de UNICEF Chile
                        que busca recaudar fondos para financiar los proyectos
                        que la organización implementa en Chile y en el mundo.
                        Se trata de cuatro corridas, en cuatro ciudades de Chile,
                        inspiradas en distintos personajes de la “Liga de la
                        Justicia” de DC Comics los que correrán para defender
                        los derechos de todos los niños, niñas y adolescentes.
                      </Card.Body>
                    </Accordion.Collapse>
                  </Card>
                  <Card>
                    <Accordion.Toggle as={Card.Header} eventKey="1">
                      ¿Cuándo y dónde se realizarán las corridas?
                    </Accordion.Toggle>
                    <Accordion.Collapse eventKey="1">
                      <Card.Body>
                        <b>
                          11 de diciembre en Santiago, en el Ciudad Empresaria,
                          Huechuraba a las 8:30 hrs, en circuitos de 2k y 5k.
                        </b>{" "}
                        <br />
                        <b>
                          22 de enero en Villarrica, en el Estadio Municipal de
                          Villarrica a las 8:30 hrs, en circuitos de 2k y 5k.
                        </b>{" "}
                        <br />
                        <b>
                          5 de febrero en Concepción, en el Estadio Municipal de
                          Concepción a las 8:30 hrs, en circuitos de 2k y 5k.
                        </b>{" "}
                        <br />
                        <b>

                          12 de febrero en Viña del Mar, en el Estadio Sausalito
                          a las 8:30 hrs, en circuitos de 2k y 5k.
                        </b>{" "}
                        <br />
                      </Card.Body>
                    </Accordion.Collapse>
                  </Card>
                  <Card>
                    <Accordion.Toggle as={Card.Header} eventKey="2">
                      ¿Cuánto cuesta participar?
                    </Accordion.Toggle>
                    <Accordion.Collapse eventKey="2">
                      <Card.Body>
                        El costo de participación es de $15.000 por persona.
                      </Card.Body>
                    </Accordion.Collapse>
                  </Card>
                  <Card>
                    <Accordion.Toggle as={Card.Header} eventKey="3">
                      ¿Cómo puedo inscribirme?
                    </Accordion.Toggle>
                    <Accordion.Collapse eventKey="3">
                      <Card.Body>
                        Puedes inscribirte en la página web de la UNICEF Heroes
                        Run, en el siguiente link:{" "}
                        <a href="https://www.unicefheroesrun.cl/">
                          https://www.unicefheroesrun.cl/
                        </a>
                      </Card.Body>
                    </Accordion.Collapse>
                  </Card>
                  <Card>
                    <Accordion.Toggle as={Card.Header} eventKey="4">
                      ¿Qué incluye la inscripción?
                    </Accordion.Toggle>
                    <Accordion.Collapse eventKey="4">
                      <Card.Body>
                        La inscripción incluye: <br />
                        - Kit de participación (polera, medalla, número de
                        corredor, bolsa de regalo, etc.) <br />
                        - Acceso a la zona de hidratación <br />
                        - Acceso a la zona de post carrera <br />
                        - Acceso a la zona de premiación <br />
                        - Acceso a la zona de entretenimiento <br />
                        - Acceso a la zona de exhibición <br />
                        - Acceso a la zona de merchandising <br />
                        - Acceso a la zona de fotografía <br />
                        - Acceso a la zona de descanso <br />
                        - Acceso a la zona de estacionamiento <br />
                        - Acceso a la zona de seguridad <br />
                        - Acceso a la zona de información <br />
                        - Acceso a la zona de baños <br />
                        </Card.Body>
                    </Accordion.Collapse>
                  </Card>
                  </Accordion>
                  </div>
                  </div>
                  </div>

        </div>
      </div>
    </ScrollContainer>
  );
}

export default App;
